// Get modal elements
document.addEventListener('DOMContentLoaded', () => {
  // Get modal elements
  const openModalButtontrailor = document.getElementById('openModalButtontrailor');
  const modaltrailor = document.getElementById('modaltrailor');
  const closeModalButtontrailor = document.getElementById('closeModalButtontrailor');

  // Open modal on button click
  openModalButtontrailor.addEventListener('click', (event) => {
    event.preventDefault();
    modaltrailor.classList.remove('hidden');
    document.body.style.overflow = 'hidden'; // Disable body scrolling
  });

  // Close modal on button click
  closeModalButtontrailor.addEventListener('click', () => {
    modaltrailor.classList.add('hidden');
    document.body.style.overflow = ''; // Re-enable body scrolling
  });

  // Close modaltrailor if user clicks outside of the modaltrailor content
  window.addEventListener('click', (event) => {
    if (event.target === modaltrailor) {
      modaltrailor.classList.add('hidden');
      document.body.style.overflow = ''; // Re-enable body scrolling
    }
  });
});


